INSERT INTO points_art_cartel (id, name, name_ru, name_en, about_title, about_title_ru, about_title_en, about_1, about_1_ru, about_1_en, about_2, about_2_ru, about_2_en, details_title, details_title_ru, details_title_en, details_1, details_1_ru, details_1_en, details_2, details_2_ru, details_2_en, details_3, details_3_ru, details_3_en, details_4, details_4_ru, details_4_en, impl_header, impl_header_ru, impl_header_en, impl_title, impl_title_ru, impl_title_en, impl_1, impl_1_ru, impl_1_en, impl_2, impl_2_ru, impl_2_en, impl_3, impl_3_ru, impl_3_en, description_1, description_1_en, description_1_ru, description_2, description_2_en, description_2_ru) VALUES (1, 'Art cartel', 'Арт-картель', 'Art cartel', 'ART CARTEL IS A FORM OF JOINT STUDY OF SUBJECTS OF INTEREST. WITHOUT A TEACHER AND A DOMINANT POINT OF VIEW, THE ONLY CORRECT POINT OF VIEW.', 'АРТ-КАРТЕЛЬ - ЭТО ФОРМА СОВМЕСТНОГО ИЗУЧЕНИЯ ИНТЕРЕСУЮЩЕГО. БЕЗ УЧИТЕЛЯ И ГОСПОДСТВУЮЩЕЙ ТОЧКИ ЗРЕНИЯ, ЕДИНСТВЕННО ПРАВИЛЬНОЙ.', 'ART CARTEL IS A FORM OF JOINT STUDY OF SUBJECTS OF INTEREST. WITHOUT A TEACHER AND A DOMINANT POINT OF VIEW, THE ONLY CORRECT POINT OF VIEW.', 'In a small group format of 8-10 people, we independently study the material and discuss it in online meetings.', 'В формате малой группы 8-10 человек мы самостоятельно изучаем материал и обсуждаем его на онлайн встречах.', 'In a small group format of 8-10 people, we independently study the material and discuss it in online meetings.', 'We search for information together and communicate a lot. We discover completely different points of view and approaches to understanding. We discuss, sometimes argue, developing new associative connections, mastering the conceptual apparatus and critical thinking.', 'Мы вместе ищем информацию и много общаемся. Мы обнаруживаем совершенно разные точки зрения и подходы к пониманию. Мы дискутируем, иногда спорим, развивая новые ассоциативные связи, овладевая понятийным аппаратом и критическим мышлением.', 'We search for information together and communicate a lot. We discover completely different points of view and approaches to understanding. We discuss, sometimes argue, developing new associative connections, mastering the conceptual apparatus and critical thinking.', 'KEY MANIFESTO OF THE ART CARTEL: THE PRIORITY OF DIFFERENT POINTS OF VIEW', 'КЛЮЧЕВОЙ МАНИФЕСТ АРТ-КАРТЕЛЯ: ГЛАВЕНСТВО РАЗНЫХ ТОЧЕК ЗРЕНИЯ', 'KEY MANIFESTO OF THE ART CARTEL: THE PRIORITY OF DIFFERENT POINTS OF VIEW', 'Art cartel is a form of joint work on self-education.INSERT INTO points_art_diving (id, name, name_ru, name_en, description, description_ru, description_en, about_title, about_title_ru, about_title_en, about_1, about_1_ru, about_1_en, about_2, about_2_ru, about_2_en, about_3, about_3_ru, about_3_en, details_title, details_title_ru, details_title_en, details_1, details_1_ru, details_1_en, details_2, details_2_ru, details_2_en, details_3, details_3_ru, details_3_en, about_b1, about_b1_en, about_b1_ru, about_b2, about_b2_en, about_b2_ru, about_b3, about_b3_en, about_b3_ru, details_b1, details_b2, details_b3, details_b1_en, details_b1_ru, details_b2_en, details_b2_ru, details_b3_en, details_b3_ru, we_can, we_can_1, we_can_1_en, we_can_1_ru, we_can_2, we_can_2_en, we_can_2_ru, we_can_3, we_can_3_en, we_can_3_ru, we_can_4, we_can_4_en, we_can_4_ru, we_can_en, we_can_ru, we_can_1_title, we_can_1_title_en, we_can_1_title_ru, we_can_2_title, we_can_2_title_en, we_can_2_title_ru, we_can_3_title, we_can_3_title_en, we_can_3_title_ru, we_can_4_title, we_can_4_title_en, we_can_4_title_ru, impl_ext_1, impl_ext_1_en, impl_ext_1_ru, impl_ext_2, impl_ext_2_en, impl_ext_2_ru, impl_ext_3, impl_ext_3_en, impl_ext_3_ru, impl_ext_4, impl_ext_4_en, impl_ext_4_ru, impl_ext_5, impl_ext_5_en, impl_ext_5_ru, impl_header, impl_header_en, impl_header_ru, impl_title_1, impl_title_1_en, impl_title_1_ru, impl_title_2, impl_title_2_en, impl_title_2_ru, impl_title_3, impl_title_3_en, impl_title_3_ru, impl_title_4, impl_title_4_en, impl_title_4_ru, impl_title_5, impl_title_5_en, impl_title_5_ru) VALUES (1, 'Art diving', 'Арт-дайвинг', 'Art diving', 'Art diving is an audio session of immersing yourself in a painting within the walls of a gallery. The viewer approaches the painting, points his smartphone at the QR code next to the painting, puts on headphones and starts a mediation session.', 'Арт-даивинг – это аудио-сеансы погружения в картину в стенах галереи. Зритель подходит к картине, наводит свой смартфон на QR-код рядом с картиной, надевает наушники и запускает сеанс медиации.', 'Art diving is an audio session of immersing yourself in a painting within the walls of a gallery. The viewer approaches the painting, points his smartphone at the QR code next to the painting, puts on headphones and starts a mediation session.', 'ART DIVING IS AN AUDIO SESSION OF DIVING INTO A PICTURE IN THE WALLS OF A GALLERY.', 'АРТ-ДАЙВИНГ - ЭТО АУДИО-СЕАНСЫ ПОГРУЖЕНИЯ В КАРТИНУ В СТЕНАХ ГАЛЕРЕИ.', 'ART DIVING IS AN AUDIO SESSION OF DIVING INTO A PICTURE IN THE WALLS OF A GALLERY.', 'Music appears as a tuning fork, setting the viewer up for leisurely contemplation.INSERT INTO points_naive_questions (id, name, description, about_title, about_1, about_2, details_title, details_1, about_1_en, about_1_ru, about_2_en, about_2_ru, about_title_en, about_title_ru, description_en, description_ru, details_1_en, details_1_ru, details_title_en, details_title_ru, impl_ext_1, impl_ext_1_en, impl_ext_1_ru, impl_ext_2, impl_ext_2_en, impl_ext_2_ru, impl_ext_3, impl_ext_3_en, impl_ext_3_ru, impl_header, impl_header_en, impl_header_ru, impl_title_1, impl_title_1_en, impl_title_1_ru, impl_title_2, impl_title_2_en, impl_title_2_ru, impl_title_3, impl_title_3_en, impl_title_3_ru, name_en, name_ru, we_can, we_can_1, we_can_1_en, we_can_1_ru, we_can_1_title, we_can_1_title_en, we_can_1_title_ru, we_can_2, we_can_2_en, we_can_2_ru, we_can_2_title, we_can_2_title_en, we_can_2_title_ru, we_can_3, we_can_3_en, we_can_3_ru, we_can_3_title, we_can_3_title_en, we_can_3_title_ru, we_can_en, we_can_ru) VALUES (1, 'Naive questions for the artist', 'Naive questions for the artist are video interviews in which the artist reveals himself from a human perspective.', 'NAIVE QUESTIONS FOR THE ARTIST is a VIDEO INTERVIEW IN WHICH THE ARTIST IS DISCOVERED FROM A HUMAN PERSPECTIVE.', 'A person is not interested in an artist, but in a person: with a human destiny, understandable joys and difficulties. The context in which the works were created, the conditions and circumstances that shaped the artist’s worldview are interesting.', 'The project “Naive Questions for the Artist” shows and talks about what precedes the birth of works - about the winding paths of an artist’s life. The viewer hears the artist’s voice, sees his facial expressions and gestures, recognizes his worldview and is able to look through the artist’s eyes at his work and feel the concept.', 'The “Naive Questions” project gives the viewer the opportunity to attune themselves to the perception of the artist’s works and think about the most ambitious and controversial work - the artist’s picture of the world', 'Through “naive questions” the artist becomes closer to the viewer.INSERT INTO points_team (id, photo, name, role, name_en, name_ru, role_en, role_ru, user_number) VALUES (1, 'team/team-photo-1_Y1I4Dt8.png', 'Smirnov Mikhail', 'Digital artist: creating music. substrates for art diving in the gallery of the Aslamazyan sisters', 'Smirnov Mikhail', 'Смирнов Михаил', 'Digital artist: creating music. substrates for art diving in the gallery of the Aslamazyan sisters', 'Digital artist: создание муз. подложки для арт-дайвинга в галерее сестер Асламазян', 1);
